import React, { Component } from 'react';
import VisibilitySensor from "react-visibility-sensor";
import liste_parcours from './data';

import './Parcours.css';

class Parcours extends Component {
  onChangeVisibility = (element) => {
    console.log(element);
    element => element.previousState=='visible';
  }
  render() {  
    return (
      <div className="animated fadeIn">
        <section className="intro">
          <div className="container">
            <h1>Mon parcours d'int√©gration &darr;</h1>
          </div>
        </section>
        <section className="timeline" id="parcours">
          <ul>
            {liste_parcours.map((element,key) => {
              return (
                <VisibilitySensor key={key} active={element.previousState!=='visible'} onChange={element=>this.onChangeVisibility(element)}>
                  {({ isVisible }) => {
                    return (
                      <li className={isVisible && 'in-view'}>
                        <div>
                          <time>{element.titre}</time> {element.description}
                        </div>
                      </li>
                    );
                  }}
                </VisibilitySensor> );
            })}
          </ul>
        </section>
      </div>
    );
  }
}

export default Parcours;
